import {
  formatBudget,
  formatDuration,
  getStatusColor,
  getTypeIcon,
} from "@/lib/utils";
import { ExportOutlined } from "@ant-design/icons";
import { Button, Modal, Tag, Tooltip, Image, Empty } from "antd";
import { MovieTVShow } from "@/types";

interface MovieDetailsProps {
  record: MovieTVShow;
}

const MovieDetails = ({ record }: MovieDetailsProps) => {
  return (
    <Tooltip title="View Details">
      <Button
        type="text"
        icon={<ExportOutlined className="text-brand-500" />}
        onClick={() => {
          Modal.info({
            title: record.title,
            width: 600,
            content: (
              <div className="space-y-4 mt-4">
                <div className="flex space-x-4">
                  {record.poster_url ? (
                    <Image
                      src={record.poster_url}
                      alt={record.title}
                      width={150}
                      height={200}
                      fallback="data:image/png;base64,"
                      className="rounded object-cover"
                    />
                  ) : (
                    <Empty description="No poster available" />
                  )}
                  <div className="grid grid-cols-1  gap-y-2 ">
                    <div>
                      <strong className="text-clip-1">Type:</strong>{" "}
                      {getTypeIcon(record.type)}
                    </div>
                    <div>
                      <strong className="text-clip-1">Director:</strong>{" "}
                      {record.director}
                    </div>
                    <div>
                      <strong className="text-clip-1">Year:</strong>{" "}
                      {record.year}
                    </div>

                    <div>
                      <strong className="text-clip-1">Rating:</strong>{" "}
                      {record.rating ? `${record.rating}/10` : "N/A"}
                    </div>
                    <div>
                      <strong className="text-clip-1">Duration:</strong>{" "}
                      {formatDuration(record.duration)}
                    </div>
                    <div>
                      <strong>Budget:</strong> {formatBudget(record.budget)}
                    </div>
                    <div>
                      <strong>Location:</strong> {record.location || "N/A"}
                    </div>
                    <div className="flex gap-x-2">
                      <strong>Status:</strong>{" "}
                      <Tag color={getStatusColor(record.status)}>
                        {record.status}
                      </Tag>
                    </div>
                    <div>
                      <strong className="text-clip-1">Genre:</strong>{" "}
                      {record.genre || "N/A"}
                    </div>
                  </div>
                </div>
                <div>
                  {record.description && (
                    <div>
                      <strong>Description:</strong>
                      <p className="mt-2 text-gray-600">{record.description}</p>
                    </div>
                  )}
                </div>
              </div>
            ),
          });
        }}
      />
    </Tooltip>
  );
};

export default MovieDetails;
