import { ColumnsType } from "antd/es/table";
import { Space, Tag, Typography, Image, Rate, Empty } from "antd";
import {
  CalendarOutlined,
  ClockCircleOutlined,
  DollarOutlined,
  EnvironmentOutlined,
} from "@ant-design/icons";
import { MovieTVShow } from "@/types";
import {
  formatBudget,
  formatDuration,
  getStatusColor,
  getTypeIcon,
} from "@/lib/utils";
import MovieDetails from "@/components/movies/MovieDetails";
import AddUpdateMovie from "@/components/movies/AddUpdateMovie";
import DeleteMovie from "@/components/movies/DeleteMovie";

const { Text: AntText } = Typography;

export const getMovieTableColumns = (): ColumnsType<MovieTVShow> => [
  {
    title: "Poster",
    dataIndex: "poster_url",
    key: "poster",
    width: 80,
    render: (poster_url: string) => (
      <div className="w-12 h-12">
        {poster_url ? (
          <Image
            src={poster_url}
            alt="Movie poster"
            className="w-full h-full object-cover rounded"
            fallback="data:image/png;base64,"
          />
        ) : (
          <Image
            alt="Movie poster"
            className="w-full h-full object-cover rounded"
            src="data:image/png;base64,"
            preview={false}
          />
        )}
      </div>
    ),
  },
  {
    title: "Title",
    dataIndex: "title",
    key: "title",
    width: 200,
    render: (title: string, record: MovieTVShow) => (
      <div className="space-y-1">
        <div className="flex items-center space-x-2">
          <span className="font-medium">{title}</span>
          <span>{getTypeIcon(record.type)}</span>
        </div>
        <AntText type="secondary" className="text-xs">
          {record.director}
        </AntText>
      </div>
    ),
  },
  {
    title: "Year",
    dataIndex: "year",
    key: "year",
    width: 80,
    render: (year: number) => (
      <div className="flex items-center space-x-1">
        <CalendarOutlined className="text-gray-400" />
        <span>{year}</span>
      </div>
    ),
  },
  {
    title: "Duration",
    dataIndex: "duration",
    key: "duration",
    width: 100,
    render: (duration: number) => (
      <div className="flex items-center space-x-1">
        <ClockCircleOutlined className="text-gray-400" />
        <span>{formatDuration(duration)}</span>
      </div>
    ),
  },
  {
    title: "Budget",
    dataIndex: "budget",
    key: "budget",
    width: 120,
    render: (budget: number) => (
      <div className="flex items-center space-x-1">
        <DollarOutlined className="text-gray-400" />
        <span>{formatBudget(budget)}</span>
      </div>
    ),
  },
  {
    title: "Location",
    dataIndex: "location",
    key: "location",
    width: 120,
    render: (location: string) => (
      <div className="flex items-center space-x-1">
        <EnvironmentOutlined className="text-gray-400" />
        <span>{location}</span>
      </div>
    ),
  },
  {
    title: "Genre",
    dataIndex: "genre",
    key: "genre",
    width: 120,
    render: (genre: string) => (
      <Tag color="blue" className="text-xs">
        {genre || "N/A"}
      </Tag>
    ),
  },
  {
    title: "Rating",
    dataIndex: "rating",
    key: "rating",
    width: 120,
    render: (rating: number) => (
      <div className="space-y-1">
        {rating ? (
          <Rate disabled defaultValue={rating} className="text-xs" />
        ) : (
          <AntText type="secondary" className="text-xs">
            No rating
          </AntText>
        )}
      </div>
    ),
  },
  {
    title: "Status",
    dataIndex: "status",
    key: "status",
    width: 140,
    render: (status: string) => (
      <Tag color={getStatusColor(status)} className="text-xs">
        {status}
      </Tag>
    ),
  },
  {
    title: "Actions",
    key: "actions",
    width: 120,
    render: (_, record: MovieTVShow) => (
      <Space size="small">
        <MovieDetails record={record} />
        <AddUpdateMovie movie={record} />
        <DeleteMovie movie={record} />
      </Space>
    ),
  },
];
